var _siYuanConfig = require("./siYuanConfig");

/* 思源 API 调用
   REF [cc-baselib/siYuanApi.js at main · leolee9086/cc-baselib](https://github.com/leolee9086/cc-baselib/blob/main/src/siYuanApi.js)
 */
async function 向思源请求数据(url, data) {
  let resData = null;
  await fetch(url, {
    body: JSON.stringify(data),
    method: 'POST',
    headers: {
      Authorization: `Token ${_siYuanConfig.config.token}`
    }
  }).then(function (response) {
    resData = response.json();
  });
  return resData;
}

async function 解析响应体(response) {
  let r = await response; // console.log(r)

  return r.code === 0 ? r.data : null;
}

async function 以sql向思源请求块数据(sql) {
  let sqldata = {
    stmt: sql
  };
  let url = '/api/query/sql';
  return 解析响应体(向思源请求数据(url, sqldata));
}

async function 向思源请求笔记本列表() {
  // eslint-disable-next-line no-undef
  let sqldata = {
    stmt: sql语句
  };
  let url = '/api/notebook/lsNotebooks';
  return 解析响应体(向思源请求数据(url, sqldata));
}

async function 获取思源块链接锚文本(链接源文本) {
  链接源文本 = 链接源文本.replace("((", "").replace("))", "");
  let sql = `select *
               from blocks
               where id = '${链接源文本}'`;
  let 临时块属性 = await 以sql向思源请求块数据(sql); //  console.log ("临时块属性",临时块属性)

  let anchor = "";

  if (临时块属性) {
    try {
      if (临时块属性[0][name]) {
        anchor = 临时块属性[0][name];
      } else if (临时块属性[0]["content"]) {
        anchor = 临时块属性[0]["content"];
      } else {
        anchor = 链接源文本;
      }
    } catch (e) {
      anchor = "解析错误";
    }
  } //   console.log("锚文本",anchor)


  return anchor;
}

