async function 获取思源块链接锚文本(链接源文本) {
    链接源文本 = 链接源文本.replace("((", "").replace("))", "")
    let sql = `select *
               from blocks
               where id = '${链接源文本}'`
    let 临时块属性 = await 以sql向思源请求块数据(sql)
    //  console.log ("临时块属性",临时块属性)
    let anchor = ""
    if (临时块属性) {
        try {
            if (临时块属性[0][
                name]) {
                anchor = 临时块属性[0][
                    name]
            } else if (临时块属性[0]["content"]) {
                anchor = 临时块属性[0]["content"]
            } else {
                anchor = 链接源文本
            }
        } catch (e) {
            anchor = "解析错误"
        }
    }
    //   console.log("锚文本",anchor)
    return anchor
}

