const nconf = require('nconf');
// const path = require('path')
// const resolve = (dir) => path.join(__dirname, dir)

export function getConf(key) {
    const confPath = "./data/conf.json" // resolve("./data/conf.json")
    nconf.file({file: confPath});

    return nconf.get(key)
}

export async function setConf(key, value) {
    const confPath = "./data/conf.json" // resolve("./data/conf.json")
    nconf.file({file: confPath});

    return new Promise((resolve, reject) => {
        nconf.set(key, value)
        nconf.save(confPath, function (err, data) {
            if (err) {
                reject(err)
            }

            if (data) {
                console.log("setConf结果=>", JSON.parse(data.toString()))
                resolve(data)
            }
        })
    })
}

