import { getSiyuanPageId } from "@/lib/util";
export default {
  name: "VuepressMain",

  data() {
    return {
      formData: {
        customSlug: ""
      }
    };
  },

  async created() {
    await this.initPage();
    console.log("VuepressMain created=>初始化页面");
