export function getConf(key) {
    const confPath = "./data/conf.json" // resolve("./data/conf.json")
    const dir = parent.window.siyuan.config.system.dataDir + "/widgets/publisher"
    console.log("dir=>", dir)
    nconf.use('file', {file: confPath, dir: dir});

    // nconf.file({file: confPath, dir: "./"});

    return nconf.get(key)
}

