import { getSiyuanPageId } from "@/lib/util";
import { publishMdContent } from "@/lib/publish/publish";
import PUBLISH_TYPE_CONSTANTS from "@/lib/publish/publishUtil";
export default {
  name: "VuepressMain",

  data() {
    return {
      formData: {
        customSlug: ""
      },
      siyuanData: {
        pageId: "",
        meta: {}
      }
    };
  },

  async created() {
    await this.initPage();
  },

  async mounted() {// console.log("VuepressMain mounted");
  },

  methods: {
    async initPage() {
      const pageId = await getSiyuanPageId(false);
      this.formData.customSlug = pageId;
      this.siyuanData.pageId = pageId; // console.log("VuepressMain初始化页面");
    },

    async publishPage() {
      await publishMdContent(this.siyuanData.pageId, PUBLISH_TYPE_CONSTANTS.API_TYPE_JVUE, this.siyuanData.meta, function () {
        console.log("页面发布到Vuepresss完毕");
      });
    }

  }
};
