import { getConf, setConf } from "@/lib/config";
import PUBLISH_TYPE_CONSTANTS from "@/lib/publish/publishUtil";
export default {
  name: "JVueSetting",

  data() {
    return {
      username: "",
      password: ""
    };
  },

  async created() {
    await this.initConf();
  },

  methods: {
    initConf() {
      const conf = getConf(PUBLISH_TYPE_CONSTANTS.API_TYPE_JVUE);
      console.log("conf=>", conf);

      if (conf) {
        this.username = conf["jvue-publish-username"];
        this.password = conf["jvue-publish-password"];
      }
    },

    async saveConf() {
      await setConf(PUBLISH_TYPE_CONSTANTS.API_TYPE_JVUE, {
        "jvue-publish-username": this.username,
        "jvue-publish-password": this.password
      });
    }

  }
};
