const showdown = require("showdown")
const converter = new showdown.Converter();

/**
 * 将Markdown转换为HTML
 * @param md Markdown
 * @returns {*} HTML
 */
export function mdToHtml(md) {
    let html = converter.makeHtml(md);
    return removeWidgetTag(html);
}

/**
 * 删除挂件的HTML
 * @param str 原字符
 * @returns {*|string} 删除后的字符
 */
function removeWidgetTag(str) {
    var regex = /<iframe.*src="\/widgets\/publisher.*<\/iframe>/g;
    return str.replaceAll(regex, "")
}

/**
 * 截取指定长度html
 * @param html html
 * @param length 长度
 * @returns {string} 结果
 */
export function parseHtml(html, length) {
    let allText = filterHtml(html);
    if (allText.length < length) {
        return allText;
    }
    return allText.substring(0, length) + "...";
}

/**
 * 去除html标签，残缺不全也可以
 * @param html html
 * @returns {string} 转换后的结果
 */
function filterHtml(html) {
    /*
     * <.*?>为正则表达式，其中的.表示任意字符，*?表示出现0次或0次以上，此方法可以去掉双头标签(双头针对于残缺的标签)
     * "<.*?"表示<尖括号后的所有字符，此方法可以去掉残缺的标签，及后面的内容
     * " "，若有多种此种字符，可用同一方法去除
     */
    html = html.replaceAll(/<.*?>/g, "");
    html = html.replaceAll(/<.*?/g, "");
    html = html.replaceAll(/\\n/g, "");
    html = html.replaceAll(/\\t/g, "");
    html = html.replace("&quot;", "");
    html = html.replace(" ", "");
    html = html.replace(":", "");
    html = html.replace(";", "");
    html = html.replace(",", "");
    html = html.replace(".", "");
    html = html.replace(".", "");
    html = html.replace("(", "");
    html = html.replace(")", "");
    html = html.replace("'", "");
    html = html.replace("\"", "");
    html = html.replace("+", "");
    html = html.replace("=", "");
    html = html.replace("-", "");
    html = html.toLowerCase();
    return html;
}
