import { covertStringToDate, formatIsoDate, formatZhDate, getSiyuanPageId, obj2yaml, yaml2Obj, zhSlugify } from "@/lib/util";
import { exportMdContent, getBlockAttrs, setBlockAttrs } from "@/lib/siYuanApi";
import { PUBLISH_POSTID_KEY_CONSTANTS } from "@/lib/publish/publishUtil";
import { slugify } from 'transliteration';
import { getPage } from "@/lib/siyuanUtil";
export default {
  name: "VuepressMain",

  data() {
    return {
      formData: {
        customSlug: "",
        created: new Date(),
        checkList: []
      },
      siyuanData: {
        pageId: "",
        meta: {}
      },
      vuepressData: {
        yamlObj: {
          title: "",
          date: new Date(),
          permalink: "/post/convert-npm-dependencies-to-local.html",
          meta: [{
            name: "keywords",
            content: ""
          }, {
            name: "description",
            content: ""
          }],
          categories: [],
          tags: [],
          author: {
            name: "terwer",
            link: "https://github.com/terwer"
          }
        },
        formatter: "",
        vuepressContent: "",
        vuepressFullContent: ""
      }
    };
  },

  async created() {
    await this.initPage();
  },

  async mounted() {},

  methods: {
    async initPage() {
      const pageId = await getSiyuanPageId(false);
      const page = await getPage(pageId);
      console.log("VuepressMain获取主文档", page); // 思源笔记数据

      this.siyuanData.pageId = pageId;
      this.siyuanData.meta = await getBlockAttrs(pageId); // 表单数据

      this.formData.customSlug = this.siyuanData.meta["custom-slug"];
      this.formData.created = page.created;
      console.log("VuepressMain初始化页面,meta=>", this.siyuanData.meta); // 表单属性转换为HTML

      this.convertAttrToYAML();
    },

    async makeSlug() {
      // 获取最新属性
      this.siyuanData.meta = await getBlockAttrs(this.siyuanData.pageId); // 获取标题

      const title = this.siyuanData.meta.title;

      if (this.formData.checkList.length > 0) {
        // 调用Google翻译API
        const result = await zhSlugify(title);
        console.log("result=>", result);

        if (result) {
          this.formData.customSlug = result;
        } else {
          alert(this.$t('main.opt.failure'));
        }
      } else {
        this.formData.customSlug = slugify(title);
      }
    },

    createTimeChanged(val) {
      console.log("createTimeChanged=>", val);
    },

    async saveAttrToSiyuan() {
      const customAttr = {
        "custom-slug": this.formData.customSlug,
        [PUBLISH_POSTID_KEY_CONSTANTS.VUEPRESS_POSTID_KEY]: this.formData.customSlug
      };
      await setBlockAttrs(this.siyuanData.pageId, customAttr);
      console.log("VuepressMain保存属性到思源笔记,meta=>", customAttr); // 刷新属性数据

      await this.initPage();
      alert(this.$t('main.opt.success'));
    },

    convertAttrToYAML() {
      console.log("convertAttrToYAML"); // 表单属性转yamlObj

      console.log("convertAttrToYAML,formData=>", this.formData);
      this.vuepressData.yamlObj.date = covertStringToDate(this.formData.created); // formatter

      let yaml = obj2yaml(this.vuepressData.yamlObj); // 修复yaml的ISO日期格式

      yaml = formatZhDate(yaml);
      this.vuepressData.formatter = yaml;
      this.vuepressData.vuepressFullContent = this.vuepressData.formatter;
    },

    async convertYAMLToAttr() {
      console.log("convertYAMLToAttr");
      this.vuepressData.formatter = this.vuepressData.vuepressFullContent;
      this.vuepressData.yamlObj = yaml2Obj(this.vuepressData.formatter); // yamlObj转表单属性

      console.log("convertYAMLToAttr,yamlObj=>", this.vuepressData.yamlObj);
      this.formData.created = formatIsoDate(this.vuepressData.yamlObj.date.toISOString());
    },

    copyToClipboard() {
      console.log("copyToClipboard");
    },

    async publishPage() {
      const data = await exportMdContent(this.siyuanData.pageId);
      this.vuepressData.vuepressContent = data.content;
      this.vuepressData.vuepressFullContent = this.vuepressData.formatter + "\n" + this.vuepressData.vuepressContent;
      alert(this.$t('main.opt.success'));
    }

  }
};
